import React, { useState, useEffect } from 'react';
// import fetchApi from '../services/fetchApi';
import PropTypes from 'prop-types';
import ContextApi from './ContextApi';

function ApiProvider({ children }) {
  const [data, setData] = useState([]);

  const context = {
    data,
    setData,
  };

  const fetchApi = async () => {
    const url = 'https://swapi-trybe.herokuapp.com/api/planets/';
    const { results } = await fetch(url).then((response) => response.json());
    const resultFilter = Object.keys(results[0]).filter((key) => key !== 'residents');
    setData(resultFilter);
  };

  useEffect(() => {
    const getResults = fetchApi();
    const columnFilter = Object.keys(getResults[0]).filter((key) => key !== 'residents');
    setData(columnFilter);
  }, []);

  // useEffect(() => {
  //   (async () => {
  //     const planets = await apiPlanets();
  //     const sortedPlanets = [...planets]
  //       .sort(({ name: a }, { name: b }) => a.localeCompare(b));
  //     setData(sortedPlanets);
  //   })();
  // }, []);

  return (
    <section>
      <ContextApi.Provider value={ context }>
        { children }
      </ContextApi.Provider>
    </section>
  );
}

ApiProvider.propTypes = {
  children: PropTypes.node.isRequired,
};

export default ApiProvider;
