import React, { useContext, useEffect } from 'react';
import ContextApi from '../context/ContextApi';
import Input from './Input';

function FilterByNumericValues() {
  const { data, filters, setFilters, setDataFilter } = useContext(ContextApi);
  // const { filterByName: { name } } = filters;

  const handleChange = ({ target: { value } }) => {
    setFilters({ ...filters, filterByName: { name: value } });
  };

  // useEffect(() => {
  //   const planetFiltering = () => {
  //     const { filterByName } = filters;
  //     const aux = [...data];
  //     // console.log(allData);
  //     setDataFilter(aux.filter((i) => i.name.toLowerCase().includes(filterByName.name)));
  //   };
  //   planetFiltering();
  // }, [data, filters, setDataFilter]);

  useEffect(() => {
    const planetByNumericValues = () => {
      const { filterByNumericValues } = filters;
      let aux = [...data];
      // console.log(allData);
      filterByNumericValues.forEach(({ column, comparison, value }) => {
        switch (comparison) {
        case 'maior que':
          aux = aux.filter((item) => (
            Number(item[column]) > value));
          setDataFilter(aux);
          break;
        case 'menor que':
          aux = aux.filter((item) => (
            Number(item[column]) < value));
          setDataFilter(aux);
          break;
        case 'igual a':
          aux = aux.filter((item) => (
            Number(item[column]) === value));
          setDataFilter(aux);
          break;
        default:
          break;
        }
      });
    };
    planetByNumericValues();
  }, [data, filters, setDataFilter]);

  return (
    <Input
      textLabel="Planeta"
      id="name-filter"
      type="text"
      // value={ name }
      onChange={ handleChange }
    />

  );
}

export default FilterByNumericValues;
