import React, { useState, useEffect, useContext } from 'react';
import ContextApi from '../context/ContextApi';

export default function SortingColumns() {
  const columnsOptions = [
    'population',
    'orbital_period',
    'diameter',
    'rotation_period',
    'surface_water',
  ];

  const { setDataFilter, filters, setFilters } = useContext(ContextApi);
  const [column, setColumn] = useState('population');
  const [sort, setSort] = useState('ASC');

  // const sortData = (planets) => {
  //   const { order } = filters;
  //   if (order !== { column: 'name', sort: 'ASC' }) {
  //     switch (sort) {
  //     case 'ASC':
  //       return planets
  //         .sort(({ [column]: a }, { [column]: b }) => a.localeCompare(b))
  //         .sort((a, b) => (a[column] - b[column]));
  //     case 'DESC':
  //       return planets
  //         .sort(({ [column]: a }, { [column]: b }) => b.localeCompare(a))
  //         .sort((a, b) => b[column] - a[column]);
  //     default:
  //       return planets;
  //     }
  //   }
  // };

  useEffect(() => {
    const aux = [...data];
    const { order } = filters;
    aux.sort((a, b) => (a.name.localeCompare(b.name)));
    if (order.sort === 'DESC') {
      aux.reverse();
    }
    setDataFilter(aux);
  });

  const renderSorted = () => {
    // console.log(sort);
    setFilters({
      ...filters,
      order: {
        column,
        sort,
      },
    });
  };

  return (
    <form>
      <select
        data-testid="column-sort"
        onChange={ ({ target: { value } }) => setColumn(value) }
      >
        {
          columnsOptions.map((option) => (<option key={ option }>{option}</option>))
        }
      </select>
      <label htmlFor="ASC">
        Crescente
        <input
          data-testid="column-sort-input-asc"
          id="ASC"
          type="radio"
          value="ASC"
          name="sort-btn"
          onChange={ ({ target: { value } }) => setSort(value) }
          checked={ sort === 'ASC' }
        />
      </label>
      <label htmlFor="DESC">
        Decrescente
        <input
          data-testid="column-sort-input-desc"
          id="DESC"
          type="radio"
          value="DESC"
          name="sort-btn"
          onChange={ ({ target: { value } }) => setSort(value) }
          checked={ sort === 'DESC' }
        />
      </label>
      <button
        data-testid="column-sort-button"
        type="button"
        onClick={ renderSorted }
      >
        Ordenar
      </button>
    </form>
  );
}
