import React, { useContext, useEffect, useState } from 'react';
import ContextApi from '../context/ContextApi';
import Input from './Input';

const compareOptions = ['maior que', 'menor que', 'igual a'];

function FilterNumericValues() {
  const {
    data,
    filters,
    setDataFilter,
    columns,
  } = useContext(ContextApi);

  const [numericFilter, setNumericFilter] = useState({});
  const { column, comparison, value } = numericFilter;
  const { filterByNumericValues } = filters;
  // const { filterByName: { name } } = filters;

  function handleChange({ target }) {
    setNumericFilter({
      ...numericFilter,
      [target.name]: target.value,
    });
  }

  function onClick() {
    setFilters({
      ...filters,
      filterByNumericValues: [
        ...filterByNumericValues,
        numericFilter,
      ],
    });
    setColumns(columns.filter((col) => !column.includes(col)));
  }

  useEffect(() => {
    setNumericFilter({
      column: columns[0],
      comparions: 'maior que',
      value: '0',
    });
  }, [columns]);

  useEffect(() => {
    const planetByValues = () => {
      let aux = [...data];
      // console.log(allData);
      filterByNumericValues.forEach(() => {
        switch (comparison) {
        case 'maior que':
          aux = aux.filter((item) => (
            Number(item[column]) > value));
          setDataFilter(aux);
          break;
        case 'menor que':
          aux = aux.filter((item) => (
            Number(item[column]) < value));
          setDataFilter(aux);
          break;
        case 'igual a':
          aux = aux.filter((item) => (
            Number(item[column]) === value));
          setDataFilter(aux);
          break;
        default:
          break;
        }
      });
    };
    planetByValues();
  }, [data, filters, setDataFilter, column, comparison, filterByNumericValues, value]);

  return (
    <>
      <select
        textLabel="column"
        id="column-filter"
        value={ column }
        onChange={ handleChange }
      >
        {
          columns.map((option) => (<option key={ option }>{option}</option>))
        }
      </select>
      <select
        textLabel="comparison"
        id="comparison-filter"
        value={ comparison }
        onChange={ handleChange }
      >
        {
          compareOptions.map((option) => (<option key={ option }>{option}</option>))
        }
      </select>
      <Input
        textLabel="value"
        id="value-filter"
        type="number"
        value={ value }
        onChange={ handleChange }
      />
      <button
        data-testid="button-filter"
        type="button"
        onClick={ onClick }
      >
        Filtrar
      </button>
    </>
  );
}

export default FilterNumericValues;
