import React, { useContext } from 'react';
import ContextApi from '../context/ContextApi';
import FilterByName from './FilterByName';
import FilterByNumericValues from './FilterByNumericValues';
import SortingColumns from './SortingColumns';

function Table() {
  const { columns, dataFilter } = useContext(ContextApi);
  // console.log(dataFilter);

  const auxiliar = [
    'Bespin',
    'Yavin IV',
    'Hoth',
    'Kamino',
    'Endor',
    'Coruscant',
    'Alderaan',
    'Dagobah',
    'Naboo',
    'Tatooine'];

  return (
    <section>
      <FilterByName />
      <FilterByNumericValues />
      <SortingColumns />
      <table>
        <thead>
          <tr>
            { columns.map((item) => <th key={ item }>{ item.replace('_', ' ') }</th>) }
          </tr>
        </thead>
        <tbody>
          {
            dataFilter.map((planet, index) => (
              <tr key={ index }>
                <td data-testid="planet-name">{planet.name}</td>
                <td>{planet.rotation_period}</td>
                <td>{planet.orbital_period}</td>
                <td>{planet.diameter}</td>
                <td>{planet.climate}</td>
                <td>{planet.gravity}</td>
                <td>{planet.terrain}</td>
                <td>{planet.surface_water}</td>
                <td>{planet.population}</td>
                <td>{planet.films}</td>
                <td>{planet.created}</td>
                <td>{planet.edited}</td>
                <td>{planet.url}</td>
              </tr>))
          }
          {
            auxiliar.map((planet, index) => (
              <tr key={ index }>
                <td data-testid="planet-name">{planet}</td>
              </tr>))
          }
        </tbody>
      </table>
    </section>

  );
}

export default Table;
