import React, { useContext, useEffect } from 'react';
import ContextApi from '../context/ContextApi';
import Input from './Input';

const compareOptions = ['maior que', 'menor que', 'igual a'];

function FilterByNumericValues() {
  const { data, filters, setFilters, setDataFilter, columns } = useContext(ContextApi);
  // const { filterByName: { name } } = filters;

  const handleChange = ({ target: { value } }) => {
    setFilters({ ...filters, filterByName: { name: value } });
  };

  useEffect(() => {
    const planetByNumericValues = () => {
      const { filterByNumericValues } = filters;
      let aux = [...data];
      // console.log(allData);
      filterByNumericValues.forEach(({ column, comparison, value }) => {
        switch (comparison) {
        case 'maior que':
          aux = aux.filter((item) => (
            Number(item[column]) > value));
          setDataFilter(aux);
          break;
        case 'menor que':
          aux = aux.filter((item) => (
            Number(item[column]) < value));
          setDataFilter(aux);
          break;
        case 'igual a':
          aux = aux.filter((item) => (
            Number(item[column]) === value));
          setDataFilter(aux);
          break;
        default:
          break;
        }
      });
    };
    planetByNumericValues();
  }, [data, filters, setDataFilter]);

  return (
    <>
      <select
        textLabel="column"
        id="column-filter"
        value={ columns }
        onChange={ handleChange }
      >
        {
          columns.map((option) => (<option key={ option }>{option}</option>))
        }
      </select>
      <select
        textLabel="comparison"
        id="comparison-filter"
        // value={ name }
        onChange={ handleChange }
      >
        {
          compareOptions.map((option) => (<option key={ option }>{option}</option>))
        }
      </select>
      <Input
        textLabel="value"
        id="value-filter"
        type="number"
        value={ value }
        onChange={ handleChange }
      />

    </>
  );
}

export default FilterByNumericValues;
