      break;
    case 'comparison-filter':
      columns.map((option) => <option key={ option }>{option}</option>);
      break;
    case 'value-filter':
      columns.map((option) => <option key={ option }>{option}</option>);
      break;
    default:
      break;
    }
  }
  return (
    <label
      htmlFor={ id }
    >

      { textLabel }

      <select
        data-testid={ id }
        // name={ name }
        id={ id }
        value={ value }
        onChange={ onChange }
      >
        {
          options()
        }
      </select>

    </label>
  );
}

Select.propTypes = {
  id: string.isRequired,
  // name: string,
  onChange: func.isRequired,
  value: string || number,
  textLabel: string,
};

Select.defaultProps = {
  // name: '',
  value: '',
  textLabel: '',
};
