import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import ContextApi from './ContextApi';

function ApiProvider({ children }) {
  const [data, setData] = useState([]);
  const [dataFilter, setDataFilter] = useState([]);
  const [columns, setColumns] = useState([]);
  const [filters, setFilters] = useState({
    filterByName: {
      name: '',
    },
    filterByNumericValues: [],
  });

  const context = {
    data,
    setData,
    columns,
    setColumns,
    filters,
    setFilters,
    dataFilter,
    setDataFilter,
  };

  const fetchApi = async () => {
    const url = 'https://swapi-trybe.herokuapp.com/api/planets/';
    const { results } = await fetch(url).then((response) => response.json());
    const columnsFilter = Object.keys(results[0]).filter((item) => item !== 'residents');
    setData(results);
    setDataFilter(results);
    setColumns(columnsFilter);
  };

  useEffect(() => {
    fetchApi();
  }, []);

  useEffect(() => {
    let aux = [...data];
    const { filterByNumericValues } = filters;
    const planetByValue = () => {
      filterByNumericValues.forEach(({ column, comparison, value }) => {
        if (comparison === 'maior que') {
          aux = aux.filter((planet) => Number(planet[column]) > Number(value));
          return setDataFilter(aux);
        }
        if (comparison === 'menor que') {
          aux = aux.filter((planet) => Number(planet[column]) < Number(value));
          return setDataFilter(aux);
        }
        if (comparison === 'igual a') {
          aux = aux.filter((planet) => Number(planet[column]) === Number(value));
          return setDataFilter(aux);
        }
      });
    };
    planetByValue();
  }, [data, setDataFilter, filters]);

  return (
    <section>
      <ContextApi.Provider value={ context }>
        { children }
      </ContextApi.Provider>
    </section>
  );
}

ApiProvider.propTypes = {
  children: PropTypes.node.isRequired,
};

export default ApiProvider;
