import React, { useState, useEffect, useContext } from 'react';
import ContextApi from '../context/ContextApi';

export default function SortingColumns() {
  const columnsOptions = [
    'population',
    'orbital_period',
    'diameter',
    'rotation_period',
    'surface_water',
  ];

  const { dataFilter, setDataFilter, filters } = useContext(ContextApi);
  const [column, setColumns] = useState(columnsOptions);
  const [sort, setSort] = useState('ASC');
  const { order } = filters;

  const renderSorted = () => {
    setDataFilter({
      ...dataFilter,
      order: {
        column,
        sort,
      },
    });
  };

  const sortColumn = () => {
    if (order !== { column: 'name', sort: 'ASC' }) {
      dataFilter.sort((a, b) => {
        switch (order.sort) {
        case 'ASC':
          return parseInt(a[order.column], 10) - parseInt(b[order.column], 10);
        case 'DESC':
          return parseInt(b[order.column], 10) - parseInt(a[order.column], 10);
        default:
          return 0;
        }
      });
    }
    return setDataFilter(dataFilter);
  };

  useEffect(() => {
    sortColumn();
  });

  return (
    <div>
      <select
        data-testid="column-sort"
        onChange={ ({ target: { value } }) => setColumns(value) }
      >
        {
          columnsOptions.map((option) => (<option key={ option }>{option}</option>))
        }
      </select>
      <label htmlFor="ASC">
        Crescente
        <input
          type="radio"
          data-testid="column-sort-input-asc"
          id="ASC"
          value="ASC"
          onChange={ ({ target: { value } }) => setSort(value) }
        />
      </label>
      <label htmlFor="DESC">
        Decrescente
        <input
          type="radio"
          data-testid="column-sort-input-desc"
          id="DESC"
          value="DESC"
          onChange={ ({ target: { value } }) => setSort(value) }
        />
      </label>
      <button
        type="button"
        data-testid="column-sort-button"
        onClick={ renderSorted }
      >
        Ordenar
      </button>
    </div>
  );
}
