import React, { useContext, useEffect } from 'react';
import ContextApi from '../context/ContextApi';
import Input from './Input';

function FilterByName() {
  const { dataFilter, filters, setFilters, setDataFilter } = useContext(ContextApi);
  const { filterByName: { name } } = filters;

  const handleChange = ({ target: { value } }) => {
    setFilters({ ...filters, filterByName: { name: value } });
  };

  useEffect(() => {
    const planetByName = () => {
      const { filterByName } = filters;
      const aux = [...dataFilter];
      const newAux = aux.filter((i) => i.name.toLowerCase().includes(filterByName.name));
      // console.log(allData);
      setDataFilter(newAux);
    };
    planetByName();
  }, [dataFilter, filters, setDataFilter]);

  return (
    <Input
      textlabel="Planeta"
      id="name-filter"
      type="search"
      value={ name }
      onChange={ handleChange }
    />

  );
}

export default FilterByName;
