import React, { useContext, useEffect, useState } from 'react';
import ContextApi from '../context/ContextApi';
import Input from './Input';

const compareOptions = ['maior que', 'menor que', 'igual a'];

function FilterNumericValues() {
  const {
    data,
    filters,
    setFilters,
    setDataFilter,
    columns,
    setColumns,
  } = useContext(ContextApi);

  const { filterByNumericValues } = filters;
  const [numericFilter, setNumericFilter] = useState({
    column: columns[0],
    comparions: 'maior que',
    value: 0,
  });
  // const { filterByName: { name } } = filters;

  useEffect(() => {
    let aux = [...data];
    const filterPlanet = () => {
      filterByNumericValues.forEach(({ column, comparison, value }) => {
        if (comparison === 'maior que') {
          aux = aux.filter((planet) => Number(planet[column]) > Number(value));
          return setDataFilter(aux);
        }
        if (comparison === 'menor que') {
          aux = aux.filter((planet) => Number(planet[column]) < Number(value));
          return setDataFilter(aux);
        }
        if (comparison === 'igual a') {
          aux = aux.filter((planet) => Number(planet[column]) === Number(value));
          return setDataFilter(aux);
        }
      });
    };
    filterPlanet();
  }, [data, filters, filterByNumericValues, setDataFilter]);

  const { column, comparison, value } = numericFilter;

  function handleChange({ target }) {
    setNumericFilter({
      ...numericFilter,
      [target.name]: target.value,
    });
  }

  function onClick() {
    setFilters({
      ...filters,
      filterByNumericValues: [
        ...filterByNumericValues,
        numericFilter,
      ],
    });
    setColumns(columns.filter((col) => !column.includes(col)));
  }

  // useEffect(() => {
